Postgres script:

{code:sql}CREATE or REPLACE FUNCTION exchange_balance (account_id bigint, amount float8) RETURNS float8 LANGUAGE plpgsql as $$
DECLARE
    current_balance float8 := 0;
    change_balance float8 := 0;
    final_balance float8 := -1;  
begin
    select balance into current_balance from money_account where id=exchange_balance.account_id;
    change_balance := current_balance + exchange_balance.amount;
	if (change_balance >= 0) THEN
        UPDATE money_account
	        SET balance = balance + amount
	        WHERE id = exchange_balance.account_id
	    RETURNING balance into final_balance;
	end if;
    return final_balance;
end
$$;{code}



